mapper.insert:
INSERT INTO page_info(USER_ID, PAGE_URL)
VALUE (
    #{userId}
    , #{pageUrl}
)

    SELECT MAX(PAGE_ID) page_id
    FROM PAGE_INFO
    WHERE   USER_ID = #{userId}
        AND PAGE_URL = #{pageUrl}

INSERT INTO user_hiGHlight(USER_ID, PAGE_ID, THEME_SN, TEXT)
VALUE (
    #{userId}
    , #{pageId}
    , #{themeSn}
    , #{text}
)

    SELECT MAX(HIGHLIGHT_ID) HIGHLIGHT_ID
    FROM user_highlight
    WHERE   USER_ID = #{userId}
    AND PAGE_ID = #{pageId}

mapper.select:
SELECT THEME_SN
FROM user_theme UT
    INNER JOIN theme_info ti on UT.THEME_ID = ti.THEME_ID
WHERE   UT.STATUS = 1
    AND USER_ID = #{userId}
    AND COLOR_HEX = #{colorHex}

SELECT THEME_SN
FROM user_theme UT
         INNER JOIN theme_info ti on UT.THEME_ID = ti.THEME_ID
WHERE   UT.STATUS = 1
  AND USER_ID = #{userId}
  AND COLOR_HEX = #{colorHex}

mapper.update:
UPDATE USER_HIGHLIGHT

    theme_sn = #{themeSn},
    text = #{text },

WHERE   HIGHLIGHT_ID = #{highlightId}
    AND USER_ID =   #{userId}

UPDATE USER_HIGHLIGHT
SET STATUS = 0
WHERE   HIGHLIGHT_ID = #{highlightId}
  AND USER_ID =   #{userId}

INSERT INTO theme_info(THEME_ID, THEME_SN, COLOR_HEX)
    VALUE(
        #{themeId}
        ,#{themeSn}
        ,#{colorHex}
    )
ON DUPLICATE KEY
    UPDATE
        COLOR_HEX = #{colorHex}
        , UPDATE_DATE = NOW()

mapper.select:
SELECT color_hex
FROM THEME_INFO
WHERE THEME_ID = #{id}
